type CssVariable = `--${string}`;
type Display = 'auto' | 'block' | 'swap' | 'fallback' | 'optional';
type NextFont = {
    className: string;
    style: {
        fontFamily: string;
        fontWeight?: number;
        fontStyle?: string;
    };
};
type NextFontWithVariable = NextFont & {
    variable: string;
};

export type { CssVariable, Display, NextFont, NextFontWithVariable };
